function hamburger(){const e=document.getElementById("hamburger"),t=document.querySelector("header");e.addEventListener("click",(()=>{t.classList.contains("closed")?t.classList.replace("closed","open"):t.classList.replace("open","closed")}))}function filterMenu(){const e=document.getElementById("filterButton");if(e){document.getElementById("filterResults").style.display="none",e.addEventListener("click",(()=>{e.classList.contains("active")?($("#filterResults").slideUp(500),e.style.backgroundColor="var(--yellow)",e.style.borderColor="black",e.classList.remove("active")):($("#filterResults").slideDown(500),e.style.backgroundColor="var(--orange)",e.style.borderColor="var(--orange)",e.classList.add("active"))}));document.querySelectorAll(".searchFilter").forEach((e=>{e.addEventListener("click",(()=>{e.classList.toggle("active")}))}))}}hamburger(),filterMenu();