function hamburger(){const e=document.getElementById("hamburger"),c=document.querySelector("header");e.addEventListener("click",(()=>{c.classList.contains("closed")?c.classList.replace("closed","open"):c.classList.replace("open","closed")}))}function simulateSearch(){document.querySelectorAll(".search").forEach((e=>{e.addEventListener("click",(()=>{window.location.href="results.html"}))}))}hamburger(),simulateSearch();