function hamburger(){const e=document.getElementById("hamburger"),t=document.querySelector("header");e.addEventListener("click",(()=>{t.classList.contains("closed")?t.classList.replace("closed","open"):t.classList.replace("open","closed")}))}hamburger();const filters=document.querySelectorAll(".searchFilter"),filterButton=document.getElementById("filterButton"),recipeCards=document.querySelectorAll(".recipeCard");function collapseFilters(){filterButton.classList.contains("open")?($("#filterResults").slideUp(500),filterButton.style.backgroundColor="var(--yellow)",filterButton.style.borderColor="black",filterButton.classList.remove("open")):($("#filterResults").slideDown(500),filterButton.style.backgroundColor="var(--orange)",filterButton.style.borderColor="var(--orange)",filterButton.classList.add("open"))}function toggleFilterButton(e){if(e.classList.toggle("active"),e.classList.contains("cuisineFilter")){document.querySelectorAll(".cuisineFilter").forEach((t=>{t!=e&&t.classList.contains("active")&&t.classList.remove("active")}))}else{document.querySelectorAll(".dietaryFilter").forEach((t=>{t!=e&&t.classList.contains("active")&&t.classList.remove("active")}))}}function applyFilters(){recipeCards.forEach((e=>{e.classList.remove("filteredCuisine","filteredDiet")})),filters.forEach((e=>{const t=e.classList.contains("cuisineFilter")?"Cuisine":"Diet",r=e.id.replace("Filter","");e.classList.contains("active")&&recipeCards.forEach((e=>{e.classList.contains(r)||e.classList.add(`filtered${t}`)}))}))}function updateResultsCount(){let e=0;recipeCards.forEach((t=>{t.classList.contains("filteredDiet")||t.classList.contains("filteredCuisine")||e++})),document.querySelector("#resultsCount h4 span").innerHTML=e.toString()}function filterMenu(){filterButton&&(filterButton.addEventListener("click",collapseFilters,!1),filters.forEach((e=>{e.addEventListener("click",(()=>{toggleFilterButton(e),applyFilters(),updateResultsCount()}))})))}document.getElementById("filterResults").style.display="none",filterMenu();const recipeError=document.getElementById("recipeError"),resultsHeaderHeight=document.getElementById("resultsHeader")?Math.ceil(document.getElementById("resultsHeader").offsetHeight):0;recipeError&&(recipeError.style.height=window.innerHeight-document.querySelector("header").offsetHeight-resultsHeaderHeight-document.querySelector("footer").offsetHeight-40+"px");